        const statusNetwork = isOnline
        const isOnline = statusNetwork ? '#080808': '#ffffff';
        <div className={css.friendlistbox}>
    <ul className={css.friendlist}>
    {friends.map(frend =>{
        return (
           
<li className={css.item} key={frend.id}>
  <span className="status"></span>
  <img class="avatar" src={frend.avatar} alt="User avatar" width="48" />
  <p class="name">{frend.name}</p>
</li>
        ) 
        })}
        </ul>
        </div>
};

export default FriendList;
